// src/Room.jsx

import React from 'react';
import { useParams, useLocation } from 'react-router-dom';
import CodeEditor from './CodeEditor'; // Import the CodeEditor component

const Room = () => {
  // Get the 'roomId' from the URL (e.g., /room/abc-123)
  const { roomId } = useParams();

  // Get the state that was passed from the Home page's navigate function
  const location = useLocation();
  const username = location.state?.username || 'Guest';

  // Now, render the CodeEditor component and pass the data to it
  return (
    <CodeEditor roomId={roomId} username={username} />
  );
};

export default Room;
