  const { roomId } = useParams();

  return (
    <div>
      <div className="room-header">
        <div className="room-id">Room ID: {roomId}</div>
        <button className="copy-btn" onClick={() => navigator.clipboard.writeText(window.location.href)}>
          Copy Link ðŸ”—
        </button>
      </div>
      <CodeEditor roomId={roomId} />
    </div>
  );
};

export default Room;
